//         <ArrowCircleRightIcon onClick={goToNext} className="right-arrow" /> */}
        
//       </div>
//       <div className='post-image' style={{ width: largestWidth, height: largestHeight, position: 'relative' }}>
//         <ArrowCircleLeftIcon onClick={goToPrevious} className="left-arrow" />
//         <ArrowCircleRightIcon onClick={goToNext} className="right-arrow" />
//         <img
//           className="image"
//           src={images[currentIndex].props.src}
//           alt={`Image ${currentIndex}`}
//         />
//       </div>
//       <div className="dots-container">
//         {images.map((image, imageIndex) => (
//           <div
//             className="dot"
//             key={imageIndex}
//             onClick={() => goToSlide(imageIndex)}
//           >
//             ●
//           </div>
//         ))}
//       </div>
//     </div>
//   );
return (
    <div className="slider">
      <div className="post-image-container" style={{ width: largestWidth, height: largestHeight }}>
        <ArrowCircleLeftIcon onClick={goToPrevious} className="left-arrow" style={{ left: 0 }}/>
        <div className='post-image' style={{ width: '100%', height: '100%' }}>
          <img
            className="image"
            src={images[currentIndex].props.src}
            alt={`Image ${currentIndex}`}
            style={{ width: '100%', height: '100%', objectFit:'contain' }}
          />
        </div>
        <ArrowCircleRightIcon onClick={goToNext} className="right-arrow" style={{ right: 0 }} />
      </div>
      <div className="dots-container">
        {images.map((image, imageIndex) => (
          <div
            className="dot"
            key={imageIndex}
            onClick={() => goToSlide(imageIndex)}
          >
            ●
          </div>
        ))}
      </div>
    </div>
  );
}

export default ImageCarousel;
