import React, {useState} from 'react';
import { useParams } from 'react-router-dom';
import './profile-header.css';
import UserPopup from '../modals/user-popup';
import EditProfileModal from '../modals/edit-profile-modal';

export default function ProfileHeader() {


    //4 Sections?
        //Profile picture left
        //Follower information right
            //Follow button conditionally below
        //Profile information section on left below profile picture - userId, username, profiledescription
    //Line underneath whole thing


    //Pass styling for Followers && Following as props


    const {userId} = useParams();
    const loggedInUserId = JSON.parse(sessionStorage.getItem('userId'))?.UserId || null;
    const isLoggedInUserProfile = userId === loggedInUserId ? true : false;
    const [imageUrl, setImageUrl] = useState('');
    const avatarStyle = {width: "40px", height: "40px"};

    const [userData, setUserData] = useState('');
    const [followers, setFollowers] = useState('');
    const [followingCount, setFollowingCount] = useState('');
    const [following, setFollowing] = useState('');
    const [isCurrentFollower, setIsCurrentFollower] = useState(false);

    const [error, setError] = useState('');

    useEffect(async () => {
        await fetchProfileInformation();
        await fetchPictureFromBlob();
        await fetchFollowers();
        await fetchFollowing();
    }, []);

    //Call to get profile information from API
    const fetchProfileInformation = async () => {
        //Fetch data
        const response = await fetch(`http://20.49.168.20:80/account/Profile/${userId}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
            },
        });

        //Handle different responses
        const dataWrapper = await response.json();
        const { statusCode, data } = dataWrapper;

        if (statusCode === 200) {
            setUserData(data);        
        } else {
            setError(dataWrapper.value);
        }
    }

    //Get url for profile picture
    const fetchPictureFromBlob = async () => {
        const url = await BlobToImage({ byteArray: userData.profilePicture });
        setImageUrl(url);      
    };
   
    //Call to get user follower information from API
    const fetchFollowers = async () => {
        //Fetch data
        const response = await fetch(`http://20.49.168.20:80/account/followers/${userId}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
            },
        });

        //Handle different responses
        const dataWrapper = await response.json();
        const { statusCode, data } = dataWrapper;

        if (statusCode === 200) {
            setFollowers(data);

            //Check if logged in user already follows profile
            if(!isLoggedInUserProfile){
                data.forEach((user, index) =>{
                    if(user.userId === loggedInUserId)
                        setIsCurrentFollower(true);
                });
            }
        } else {
            setError(dataWrapper.value);
        }
    }

    //Call to get user follower information from API
    const fetchFollowing = async () => {
        //Fetch data
        const response = await fetch(`http://20.49.168.20:80/account/followers/${userId}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
            },
        });

        //Handle different responses
        const dataWrapper = await response.json();
        const { statusCode, data } = dataWrapper;

        if (statusCode === 200) {
            setFollowing(data);
            setFollowingCount(data.length);
        } else {
            setError(dataWrapper.value);
        }
    }

    return (
        <>
            <div className='profile-header'>
                <div className='profile-picture'>                
                    {imageUrl ? (
                        <Avatar style={{ avatarStyle }} src={imageUrl.original} />
                    ) : (
                        <Avatar style={{ avatarStyle }}>
                            {userData.forename.charAt(0).toUpperCase()}
                        </Avatar>
                    )}                
                </div>
                <div className='follow'>
                    <UserPopup
                        title="Followers"
                        data={followers}
                        buttonComponent={<span style={{ color: 'blue' }}>{userData.followerCount} likes</span>}
                    />
                    <UserPopup
                        title="Following"
                        data={following}
                        buttonComponent={<span style={{ color: 'blue' }}>{followingCount} likes</span>}
                    />
                </div>
                <div className='follow-button'>
                    {!isLoggedInUserProfile && !isCurrentFollower (
                        <button className='follow' disabled={isCurrentFollower}>
                            {!isCurrentFollower ? Follow : Following}
                        </button>
                    )}
                </div>
                <div className='profile-information'>
                    <span>{userId}</span>
                    <span>{userData.forename + ' ' + userData.surname}</span>
                    <span>{userData.profileDescription}</span>
                    {isLoggedInUserProfile && (                        
                        <EditProfileModal/>
                    )}
                </div>
                <div className='error'>
                    {error && <p className="error-message">{error}</p>}
                </div>
            </div>
        </>
    )
}
