import React, { useState, useEffect, useRef } from 'react';

const TestData = () => {
  const [data, setData] = useState([]);
  const containerRef = useRef(null);
  const pageSize = 10; // Number of data points to load per page
  const maxIterations = 20; // Maximum number of iterations to prevent infinite loop
  const originalWindowHeight = window.innerHeight;

  // Simulated function to generate test data
  const generateTestData = (numberOfPoints) => {
    const testData = [];
    for (let i = 1; i <= numberOfPoints; i++) {
      testData.push({ id: i, name: `Data Point ${i}` });
    }
    return testData;
  };

  useEffect(() => {
    let iterations = 0;

    const loadData = () => {
      while (true) {
        if (containerRef.current.clientHeight > originalWindowHeight.current) {
          break; // Break when the container height exceeds the original window height
        }

        if (iterations >= maxIterations) {
          console.log('Broken due to max iterations');
          break; // Break after 20 iterations
        }

        const currentPage = Math.ceil(data.length / pageSize) + 1;
        const startIndex = (currentPage - 1) * pageSize;
        const endIndex = startIndex + pageSize;
        const newData = generateTestData(endIndex); // Generate test data up to the endIndex
        setData((prevData) => [...prevData, ...newData]);

        console.log(`Iteration: ${iterations}`)
        iterations++;
      }
    };

    loadData();
  }, [data]);

  return (
    <div ref={containerRef}>
      <h1>Test Data</h1>
      <ul>
        {data.map((item) => (
          <li key={item.id}>{item.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default TestData;
