import { useState, useEffect } from 'react'
import moment from 'moment'
import Comment from '../comment/comment';
import RefreshLogin from '../../util/refresh-login';
import CancelOutlinedIcon from '@mui/icons-material/CancelOutlined';
import SendIcon from '@mui/icons-material/Send';
import ChatBubbleOutlineIcon from "@mui/icons-material/ChatBubbleOutline";
import "./comments-popup.css"

export default function CommentsPopup({postId, commentData}) {
    const [combinedData, setCombinedData] = useState([]);
    const [modal, setModal] = useState(false);
    const [comment, setComment] = useState('');
    const ipAddress = JSON.parse(sessionStorage.getItem('ipAddress'))?.ipAddress || null;

    //Modal logic
    const toggleModal = () => {
        setModal(!modal)
    }

    if(modal) {
        document.body.classList.add('active-modal')
    } else {
        document.body.classList.remove('active-modal')
    }
    
    //Load initial comments data
    useEffect(async() =>{       

        const fetchData = async () => {
            const userIds = commentData.map((comment) => comment.authorId);
            
            const objMap = { userIds };
            const finalBody = JSON.stringify(objMap);

            try{
                //Send data              
                const response = await fetch(ipAddress + '/account/Profiles', {
                    method: 'PUT',
                    credentials: 'include',
                    body: finalBody,
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });

                if (response.ok){
                    const data = await response.json();

                    //Map response and update
                    const userId = JSON.parse(sessionStorage.getItem('userId'))?.UserId || null;
                    const combined = commentData.map((item, index) => ({
                        userId: item.authorId,
                        name: data[index].forename + ' ' + data[index].surname,
                        profilePictureBlob: data[index].profilePictureBlob,
                        commentId: item.commentId,
                        comment: item.comment,
                        postedAt: item.postedAt,
                        likes: item.likes ? item.likes.length : 0,
                        hasUserLiked: item.likes && item.likes.some(like => like.userId === userId)
                    }));        
                    setCombinedData(combined);

                }else if(response.status === 401){
                    RefreshLogin();
                    fetchData();
                }
            }catch(error){
                console.log(error);
            }
        }
        fetchData();
    }, []);


    const handleInputChange = (e) => {
        setComment(e.target.value);
    };

    //Handle submitting comment
    const handleSubmit = async (event) => {
        event.preventDefault();

        const authorId = JSON.parse(sessionStorage.getItem('userId'))?.UserId || null;
        
        const objMap = { postId, authorId, comment };
        const finalBody = JSON.stringify(objMap);

        try{
            //Send data
            const responseComment = await fetch(ipAddress + '/posts/comment', {
                method: 'PUT',
                credentials: 'include',
                body: finalBody,
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            if(responseCmment.ok){
                //Call to retrieve user data      
                const responseUser = await fetch(ipAddress + `/account/Profile/${userId}`, {
                    method: 'GET',
                    mode: 'cors',
                    credentials: 'include',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });

                if(responseUser.status === 401){
                    RefreshLogin();
                    handleSubmit();
                }

                //Handle different responses
                const dataWrapper = await responseUser.json();
                const { statusCode, data } = dataWrapper;

                const newData = {
                    userId: authorId,
                    name:  data.forename + ' ' + data.surname,
                    profilePictureBlob: data.profilePictureBlob,
                    comment: comment,
                    postedAt: moment().local().format('yyyy/MM/DD HH:mm'),
                    likes: 0,
                    hasUserLiked: false
                };
                setCombinedData(prevData => [...prevData, newData]);

                setComment('');
            }else if(responseComment.status === 401){
                RefreshLogin();
                handleSubmit(event);
            }
        }catch(error){
            console.log(error);
        }
    };
 
  return (
    <>
      <ChatBubbleOutlineIcon onClick={toggleModal}/>

      {modal && (
        <div className="modal">
          <div onClick={toggleModal} className="overlay"></div>
            <div className="modal-content">
                <h1>{Comments}</h1>                               
                {combinedData.map((data, index) => {
                    return (
                        <Comment key={index} data={data} />
                    );
                })}

                <div className='modal-footer'>
                    <input
                        className="comment-input"
                        type="text"
                        value={comment}
                        onChange={handleInputChange}
                        placeholder='Enter a comment'
                    />                    
                    <button className="submit-modal" onClick={handleSubmit}>
                        <SendIcon className='sendIcon'/>
                    </button>
                </div>
                <button className="close-modal" onClick={toggleModal}>
                    <CancelOutlinedIcon className='closeIcon'/>
                </button>
            </div>
        </div>
      )}      
    </>
  )
}
